  color: #a6a6a6;
  margin-right: 12px;
`

const StateContent = styled.div`
  ${(props) => props.space &&
    css`
      margin-left: 16px;
    `
  }
    >div {
    font-size : 20px;
    text-align : left;
    margin-left : 30px;
    }
`

const BtnDiv = styled.div`
  padding: 20px;
`

const MessageBtn = styled.button`
  width: 240px;
  height: 60px;
  background: #0095C8;
  color: #fff;
  outline: none;
  border: none;
  cursor: pointer;
  font-size: 20px;
  font-weight: bold;
`
const ProductDiv = styled.div` 

`
const DetailAll = styled.div` 
  width: 856px;
  height : 1300px;
  background-color : white;
  display: flex;
  margin-top : 40px;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.25);
  border-radius: 30px;

`
const H4 = styled.h4` 
    width : 321px;
    height : 40px;
    font-size : 20px;
    font-weight : bold;
    margin : 40px 0 0 40px;
    text-align : left;
`

const DetailDiv = styled.div` 
  >img {
      width : 783px;
      height : 472px;
      border-radius: 30px;
      padding :20px
    }
`
const MyInfo = styled.div` 
  width: 301px;
  height : 400px;
  background-color : white;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.25);
  border-radius: 30px;
  float : right;
  padding-bottom : 20px;
`
const Logo = styled.h5`
  width: 84px;
  height: 84px;
  color: white;
  font-size: 50px;
  line-height : 78px;
  background-color: #0095c8;
  border: 1px solid;
  border-radius: 60px;
  margin: 0 0 20px 0;
`;
const LogoDiv = styled.div ` 
  display : flex;
  margin-left: 20px;
`
const Mydiv = styled.div ` 
  font-size : 20px;
  font-weight : bold;
  padding : 20px;
  margin : 20px 0 10px 0;
  display: flex;
`

const Sellerstate = styled.div`
  display: flex;
  padding : 15px;
  margin-left : 15px;
  margin-top : 10px;
  font-weight : bold;
`

const SellerContent = styled.div`
  margin-right: 12px;
  font-weight : bold;
  `
export default Detail;
