import React, { useState, Component } from 'react'
import { useDispatch } from 'react-redux';
import { registerUser } from './_actions/user_action';
import Axios from 'axios';
import { withRouter } from 'react-router-dom';
import styled from "styled-components";

const Departments = [
  { key: 1, value: "기계" },
  { key: 2, value: "메카트로닉스" },
  { key: 3, value: "소방안전설비" },
  { key: 4, value: "자동차" },
  { key: 5, value: "산업경영" },
  { key: 6, value: "토목환경" },
  { key: 7, value: "건축" },
  { key: 8, value: "실내디자인" },
  { key: 9, value: "전기" },
  { key: 10, value: "디지털전자" },
  { key: 11, value: "전자통신" },
  { key: 12, value: "방송음향영상" },
  { key: 13, value: "컴퓨터소프트웨어" },
  { key: 14, value: "모바일인터넷" },
  { key: 15, value: "자동화시스템" },
  { key: 16, value: "의공융합" },
  { key: 17, value: "경영" },
  { key: 18, value: "사회복지" },
  { key: 19, value: "세무회계" },
  { key: 20, value: "항공서비스" },
  { key: 21, value: "호텔관광" },
  { key: 22, value: "비서사무행정" },
  { key: 23, value: "도서관정보" },
  { key: 24, value: "유아교육" },
  { key: 25, value: "스포츠지도" },
  { key: 26, value: "호텔조리전공" },
  { key: 27, value: "베이커리카페전공" },
  { key: 28, value: "언어재활" }


]


function RegisterPage(props) {


  const [Email, setEmail] = useState("")
  const [Name, setName] = useState("")
  const [Password, setPassword] = useState("")
  const [ConfirmPassword, setConfirmPassword] = useState("")
  const [SchoolNum, setSchoolNum] = useState("")
  const [Department, setDepartment] = useState("1")


  const onEmailHandler = (event) => {
    setEmail(event.currentTarget.value)
  }

  const onNameHandler = (event) => {
    setName(event.currentTarget.value)
  }

  const onPasswordHandler = (event) => {
    setPassword(event.currentTarget.value)
  }

  const onConfirmPasswordHandler = (event) => {
    setConfirmPassword(event.currentTarget.value)
  }

  const onSchoolNumHandler = (event) => {
    setSchoolNum(event.currentTarget.value)
  }

  const onDepartmentHandler = (event) => {
    setDepartment(event.currentTarget.value)
  }


  const onSubmitHandler = (event) => {
    event.preventDefault();

    if (Password !== ConfirmPassword) {
      return alert('비밀번호와 비밀번호 확인은 같아야 합니다.')
    }

    let body = {
      email: Email,
      password: Password,
      name: Name
    }

  }
  const Paging = [];
  var i = 0;

  for (i; i < 2; i++) {
    if (i === 1) {
      Paging.push(<Oval key={i} />);
    } else {
      Paging.push(<Circle key={i} />);
    }
  }

  return (
    <Container>
      <Backgroud onClick={props.sign2_Open} />
    <form onSubmit={onSubmitHandler}>
      <SignDiv>
        <SignBorder>
        <Close onClick={props.sign2_Open} ><img src="./icon/clear-24px.svg" alt=""/></Close>
          <PagingDiv>{Paging}</PagingDiv>
          <Subtitle>회원가입을 진행해주세요.</Subtitle>
          <Step>STEP 02</Step>
          <P>*는 필수 정보이므로 꼭 입력해주셔야 합니다.</P>
          <InputDiv>
            <NameInput type="text" value={Name} onChange={onNameHandler} placeholder="*성명" />
            <IdInput type="email" value={Email} onChange={onEmailHandler} placeholder="*아이디" />
