const P = styled.p `
  font-size: 14px;
  margin-bottom: 36px;

  @media all and (max-width: 425px) {
    font-size: 14px;
  }
`

const InputDiv = styled.div`
  width: 450px;
  margin: 0 auto;
  margin-bottom: 20px;

  @media all and (max-width: 425px) {
    width: 270px;
  }
`;

const NameInput = styled.input.attrs({ type: "text" })`
  width: 418px;
  height: 21px;
  padding: 15px;
  border-radius: 10px;
  border: 1px solid #c2c2c2;
  &:focus,
  :hover {
    outline: none;
    border-color: #0095c8;
  }

  @media all and (max-width: 425px) {
    width: 248px;
    height: 8px;
    font-size: 12px;
    padding: 10px;
    border-radius: 5px;
  }
`;

const IdInput = styled.input.attrs({ type: "text" })`
  width: 278px;
  height: 18px;
  padding: 15px;
  border-radius: 10px;
  border: 1px solid #c2c2c2;
  margin-top: 15px;

  &:focus,
  :hover {
    outline: none;
    border-color: #0095c8;
  }

  @media all and (max-width: 425px) {
    width: 248px;
    height: 8px;
    font-size: 12px;
    padding: 10px;
    border-radius: 5px;
  }
`

const SubmitBtn = styled.button `
  width: 132px;
  height: 50px;
  border-radius: 10px;
  border: none;
  background: #C2C2C2;
  float: right;
  color: #fff;
  margin-top: 15px;
  font-size: 14px;
  margin-left: 8px;
  cursor: pointer;

  @media all and (max-width: 425px) {
    width: 110px;
    height: 18px;
    font-size: 12px;
    margin-top: 5px;
    margin-left: 0;
    background: none;
    color: #000;
    float: left;
  }
`

const PwdInput = styled.input.attrs({ type: "password" }) `
  width: 418px;
  height: 21px;
  padding: 15px;
  border-radius: 10px;
  border: 1px solid #C2C2C2;
  margin-top: 15px;
  &:focus,
  :hover {
    outline: none;
    border-color: #0095c8;
  }

  @media all and (max-width: 425px) {
    width: 248px;
    height: 8px;
    font-size: 12px;
    padding: 10px;
    border-radius: 5px;
  }
`

const EmailInput = styled.input.attrs({ type: "text" })`
  width: 133px;
  height: 18px;
  padding: 15px;
  border-radius: 10px;
  border: 1px solid #c2c2c2;
  margin-top: 15px;

  &:focus,
  :hover {
    outline: none;
    border-color: #0095c8;
  }

  @media all and (max-width: 425px) {
    width: 152px;
    height: 8px;
    font-size: 12px;
    padding: 10px;
    border-radius: 5px;
    float: left;
  }
`

const EmailDiv = styled.div `
  width: 102px;
  font-size: 14px;
  display: inline-block;
  -ms-user-select: none; 
  -moz-user-select: -moz-none; 
  -webkit-user-select: none; 
  -khtml-user-select: none; 
  user-select:none;
  margin-left: 21.5px;
  margin-right: 21.5px;

  @media all and (max-width: 425px) {
    width: 86px;
    font-size: 12px;
    margin: 0;
    margin-top: 22px;
  }
`

const PhoneCer = styled.button `
  width: 450px;
  height: 50px;
  margin-top: 15px;
  background-color: #c2c2c2;
  color: #fff;
  border: none;
  font-size: 20px;
  cursor: pointer;

  @media all and (max-width: 425px) {
    width: 270px;
    height: 35px;
  }
`

const NextBtn = styled.button`
  width: 450px;
  height: 50px;
  margin-top: 15px;
  background-color: #0095c8;
  color: #fff;
  border: none;
  font-size: 20px;
  cursor: pointer;
  margin-bottom: 32px;

  @media all and (max-width: 425px) {
    width: 270px;
    height: 35px;
    margin-top: 5px;
  }
`;

class SignPage3 extends Component {
  render() {
    const Paging = [];
    var i = 0;

    for (i; i < 3; i++) {
      if (i === 2) {
        Paging.push(<Oval key={i} />);
      } else {
        Paging.push(<Circle key={i} />);
      }
    }

    return (
      <SignDiv>
        <SignBorder>
          <PagingDiv>{Paging}</PagingDiv>
          <Subtitle>회원가입을 진행해주세요.</Subtitle>
          <Step>STEP 03</Step>
          <P>*는 필수 정보이므로 꼭 입력해주셔야 합니다.</P>
          <InputDiv>
            <NameInput placeholder="*성명" />
            <IdInput placeholder="*아이디" />
            <SubmitBtn>아이디 중복 체크</SubmitBtn>
            <PwdInput placeholder="*비밀번호" />
            <PwdInput placeholder="*비밀번호 확인" />
            <EmailInput placeholder="*대림대학교 이메일" /> 
            <EmailDiv>@email.daelim </EmailDiv>
            <SubmitBtn>이메일 인증 요청</SubmitBtn>
            <PhoneCer>인증 요청</PhoneCer>
          </InputDiv>
          <NextBtn>가입 완료</NextBtn>
        </SignBorder>
      </SignDiv>
    );
  }
}

export default SignPage3;
